import os

from matplotlib import pyplot as plt

from visualization.plotter import Plotter


class BarPlotter(Plotter):
    def __init__(self, columns):
        self.columns = columns

    def plot(self, dfs, exp_names, output_dir):
        for col in self.columns:
            values = []
            for df in dfs:
                if col in df.columns:
                    # Use explicit value from the 'All Datasets' row instead of column mean
                    if 'All Datasets' in df.index:
                        values.append(df.loc['All Datasets', col])
                    else:
                        raise ValueError(f"Row 'All Datasets' not found in DataFrame for experiment.")
                else:
                    raise ValueError(f"Column '{col}' not found in DataFrame for experiment.")
            plt.figure()
            plt.bar(exp_names, values)
            plt.title(f"{col} across experiments")
            plt.ylabel(col)
            plt.xticks(rotation=45, ha='right')
            plt.tight_layout()
            out_path = os.path.join(output_dir, f"{col}_bar_chart.png")
            plt.savefig(out_path)
            plt.close()
            print(f"Saved bar chart for '{col}' to {out_path}")

import os

from matplotlib import pyplot as plt

from visualization.plotter import Plotter


class BarPlotter(Plotter):
    def __init__(self, columns):
        self.columns = columns

    def plot(self, dfs, exp_names, output_dir):
        for col in self.columns:
            values = []
            for df in dfs:
                if col in df.columns:
